async function getBalanceOf(web3: any, tokenAddress: string, owner: string) {
  const data = web3.eth.abi.encodeFunctionCall({
    name: 'balanceOf',
    type: 'function',
    inputs: [{
      type: 'address',
      name: '_owner',
    }],
  }, [owner]);

  const balance = await web3.eth.call({
    to: tokenAddress,
    data,
  });

  return parseFloat(web3.utils.fromWei(balance.toString()));
}

